function Donations_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Donations');
	if(!$arrPerm['insert']) return false;

	$data = [
		'DonationDate' => Request::dateComponents('DonationDate', ''),
		'Amount' => Request::val('Amount', ''),
		'Description' => Request::val('Description', ''),
		'SupporterID' => Request::lookup('SupporterID', ''),
		'CampaignID' => Request::lookup('CampaignID', ''),
		'Paytype' => Request::val('Paytype', ''),
		'Number' => Request::val('Number', ''),
		'TransNo' => Request::val('TransNo', ''),
		'Matching' => Request::checkBox('Matching', ''),
		'Anonymous' => Request::checkBox('Anonymous', ''),
		'Acknowledged' => Request::checkBox('Acknowledged', ''),
		'Notes' => br2nl(Request::val('Notes', '')),
		'MemoryOf' => Request::val('MemoryOf', ''),
		'HonorOf' => Request::val('HonorOf', ''),
	];

	if($data['Amount'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Amount': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['Paytype'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Payment Type': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: Donations_before_insert
	if(function_exists('Donations_before_insert')) {
		$args = [];
		if(!Donations_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Donations', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Donations', $recID, calculated_fields()['Donations']);

	// hook: Donations_after_insert
	if(function_exists('Donations_after_insert')) {
		$res = sql("SELECT * FROM `Donations` WHERE `ID`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!Donations_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('Donations', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) Donations_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

