	// open the detail view template
	if($dvprint) {
		$template_file = is_file("./{$TemplateDVP}") ? "./{$TemplateDVP}" : './templates/Donations_templateDVP.html';
		$templateCode = @file_get_contents($template_file);
	} else {
		$template_file = is_file("./{$TemplateDV}") ? "./{$TemplateDV}" : './templates/Donations_templateDV.html';
		$templateCode = @file_get_contents($template_file);
	}

	// process form title
	$templateCode = str_replace('<%%DETAIL_VIEW_TITLE%%>', 'Donation Details', $templateCode);
	$templateCode = str_replace('<%%RND1%%>', $rnd1, $templateCode);
	$templateCode = str_replace('<%%EMBEDDED%%>', (Request::val('Embedded') ? 'Embedded=1' : ''), $templateCode);
	// process buttons
	if($AllowInsert) {
		if(!$selected_id) $templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-success" id="insert" name="insert_x" value="1" onclick="return Donations_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save New'] . '</button>', $templateCode);
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="insert" name="insert_x" value="1" onclick="return Donations_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save As Copy'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '', $templateCode);
	}

	// 'Back' button action
	if(Request::val('Embedded')) {
		$backAction = 'AppGini.closeParentModal(); return false;';
	} else {
		$backAction = '$j(\'form\').eq(0).attr(\'novalidate\', \'novalidate\'); document.myform.reset(); return true;';
	}

	if($selected_id) {
		if(!Request::val('Embedded')) $templateCode = str_replace('<%%DVPRINT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="dvprint" name="dvprint_x" value="1" onclick="$j(\'form\').eq(0).prop(\'novalidate\', true); document.myform.reset(); return true;" title="' . html_attr($Translation['Print Preview']) . '"><i class="glyphicon glyphicon-print"></i> ' . $Translation['Print Preview'] . '</button>', $templateCode);
		if($AllowUpdate)
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" onclick="return Donations_validateData();" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		else
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);

		if($AllowDelete)
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		else
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		// if not in embedded mode and user has insert only but no view/update/delete,
		// remove 'back' button
		if(
			$arrPerm['insert']
			&& !$arrPerm['update'] && !$arrPerm['delete'] && !$arrPerm['view']
			&& !Request::val('Embedded')
		)
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
		elseif($separateDV)
			$templateCode = str_replace(
				'<%%DESELECT_BUTTON%%>', 
				'<button
					type="submit" 
					class="btn btn-default" 
					id="deselect" 
					name="deselect_x" 
					value="1" 
					onclick="' . $backAction . '" 
					title="' . html_attr($Translation['Back']) . '">
						<i class="glyphicon glyphicon-chevron-left"></i> ' .
						$Translation['Back'] .
				'</button>',
				$templateCode
			);
		else
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#DonationDate').prop('readonly', true);\n";
		$jsReadOnly .= "\tjQuery('#DonationDateDay, #DonationDateMonth, #DonationDateYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#Amount').replaceWith('<div class=\"form-control-static\" id=\"Amount\">' + (jQuery('#Amount').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Description').replaceWith('<div class=\"form-control-static\" id=\"Description\">' + (jQuery('#Description').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#SupporterID').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#SupporterID_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#CampaignID').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#CampaignID_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('input[name=Paytype]').parent().html('<div class=\"form-control-static\">' + jQuery('input[name=Paytype]:checked').next().text() + '</div>')\n";
		$jsReadOnly .= "\tjQuery('#Number').replaceWith('<div class=\"form-control-static\" id=\"Number\">' + (jQuery('#Number').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#TransNo').replaceWith('<div class=\"form-control-static\" id=\"TransNo\">' + (jQuery('#TransNo').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Matching').prop('disabled', true);\n";
		$jsReadOnly .= "\tjQuery('#Anonymous').prop('disabled', true);\n";
		$jsReadOnly .= "\tjQuery('#Acknowledged').prop('disabled', true);\n";
		$jsReadOnly .= "\tjQuery('#Notes').replaceWith('<div class=\"form-control-static\" id=\"Notes\">' + (jQuery('#Notes').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#MemoryOf').replaceWith('<div class=\"form-control-static\" id=\"MemoryOf\">' + (jQuery('#MemoryOf').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#HonorOf').replaceWith('<div class=\"form-control-static\" id=\"HonorOf\">' + (jQuery('#HonorOf').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace(
		'<%%COMBO(DonationDate)%%>', 
		($selected_id && !$arrPerm['edit'] && ($noSaveAsCopy || !$arrPerm['insert']) ? 
			'<div class="form-control-static">' . $combo_DonationDate->GetHTML(true) . '</div>' : 
			$combo_DonationDate->GetHTML()
		), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(DonationDate)%%>', $combo_DonationDate->GetHTML(true), $templateCode);
	$templateCode = str_replace('<%%COMBO(SupporterID)%%>', $combo_SupporterID->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(SupporterID)%%>', $combo_SupporterID->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(SupporterID)%%>', urlencode($combo_SupporterID->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(CampaignID)%%>', $combo_CampaignID->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(CampaignID)%%>', $combo_CampaignID->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(CampaignID)%%>', urlencode($combo_CampaignID->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(Paytype)%%>', $combo_Paytype->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Paytype)%%>', $combo_Paytype->SelectedData, $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['SupporterID' => ['Supporters', 'Supporter'], 'CampaignID' => ['Campaigns', 'Campaign'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-default add_new_parent" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus text-success"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(ID)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(DonationDate)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Amount)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Description)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(SupporterID)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(CampaignID)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Paytype)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Number)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(TransNo)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Matching)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Anonymous)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Acknowledged)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(DonationYear)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Notes)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(MemoryOf)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(HonorOf)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ID)%%>', safe_html($urow['ID']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ID)%%>', html_attr($row['ID']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ID)%%>', urlencode($urow['ID']), $templateCode);
		$templateCode = str_replace('<%%VALUE(DonationDate)%%>', app_datetime($row['DonationDate']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(DonationDate)%%>', urlencode(app_datetime($urow['DonationDate'])), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Amount)%%>', safe_html($urow['Amount']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Amount)%%>', html_attr($row['Amount']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Amount)%%>', urlencode($urow['Amount']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Description)%%>', safe_html($urow['Description']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Description)%%>', html_attr($row['Description']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Description)%%>', urlencode($urow['Description']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(SupporterID)%%>', safe_html($urow['SupporterID']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(SupporterID)%%>', html_attr($row['SupporterID']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(SupporterID)%%>', urlencode($urow['SupporterID']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(CampaignID)%%>', safe_html($urow['CampaignID']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(CampaignID)%%>', html_attr($row['CampaignID']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(CampaignID)%%>', urlencode($urow['CampaignID']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Paytype)%%>', safe_html($urow['Paytype']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Paytype)%%>', html_attr($row['Paytype']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Paytype)%%>', urlencode($urow['Paytype']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Number)%%>', safe_html($urow['Number']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Number)%%>', html_attr($row['Number']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Number)%%>', urlencode($urow['Number']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(TransNo)%%>', safe_html($urow['TransNo']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(TransNo)%%>', html_attr($row['TransNo']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(TransNo)%%>', urlencode($urow['TransNo']), $templateCode);
		$templateCode = str_replace('<%%CHECKED(Matching)%%>', ($row['Matching'] ? "checked" : ""), $templateCode);
		$templateCode = str_replace('<%%CHECKED(Anonymous)%%>', ($row['Anonymous'] ? "checked" : ""), $templateCode);
		$templateCode = str_replace('<%%CHECKED(Acknowledged)%%>', ($row['Acknowledged'] ? "checked" : ""), $templateCode);
		$templateCode = str_replace('<%%VALUE(DonationYear)%%>', safe_html($urow['DonationYear']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(DonationYear)%%>', urlencode($urow['DonationYear']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(Notes)%%>', safe_html($urow['Notes']), $templateCode);
		} else {
			$templateCode = str_replace('<%%VALUE(Notes)%%>', safe_html($urow['Notes'], true), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(Notes)%%>', urlencode($urow['Notes']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(MemoryOf)%%>', safe_html($urow['MemoryOf']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(MemoryOf)%%>', html_attr($row['MemoryOf']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(MemoryOf)%%>', urlencode($urow['MemoryOf']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(HonorOf)%%>', safe_html($urow['HonorOf']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(HonorOf)%%>', html_attr($row['HonorOf']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(HonorOf)%%>', urlencode($urow['HonorOf']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(ID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(DonationDate)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(DonationDate)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Amount)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Amount)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Description)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Description)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(SupporterID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(SupporterID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(CampaignID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(CampaignID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Paytype)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Paytype)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Number)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Number)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(TransNo)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(TransNo)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%CHECKED(Matching)%%>', '', $templateCode);
		$templateCode = str_replace('<%%CHECKED(Anonymous)%%>', '', $templateCode);
		$templateCode = str_replace('<%%CHECKED(Acknowledged)%%>', '', $templateCode);
		$templateCode = str_replace('<%%VALUE(DonationYear)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(DonationYear)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Notes)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Notes)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(MemoryOf)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(MemoryOf)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(HonorOf)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(HonorOf)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Donations');
	if($selected_id) {
		$jdata = get_joined_record('Donations', $selected_id);
		if($jdata === false) $jdata = get_defaults('Donations');
		$rdata = $row;
	}
	$templateCode .= loadView('Donations-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: Donations_dv
	if(function_exists('Donations_dv')) {
		$args = [];
		Donations_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}