function Supporters_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Supporters');
	if(!$arrPerm['insert']) return false;

	$data = [
		'FirstName' => Request::val('FirstName', ''),
		'LastName' => Request::val('LastName', ''),
		'SpouseName' => Request::val('SpouseName', ''),
		'Business' => Request::val('Business', ''),
		'Address1' => Request::val('Address1', ''),
		'Address2' => Request::val('Address2', ''),
		'City' => Request::val('City', ''),
		'State' => Request::val('State', ''),
		'Zip' => Request::val('Zip', ''),
		'Country' => Request::val('Country', 'United States'),
		'Phone' => Request::val('Phone', ''),
		'Cell' => Request::val('Cell', ''),
		'Email' => Request::val('Email', ''),
		'Status' => Request::val('Status', 'Active'),
		'ContactMethod' => Request::multipleChoice('ContactMethod', ''),
		'Grouping' => Request::val('Grouping', ''),
	];


	// hook: Supporters_before_insert
	if(function_exists('Supporters_before_insert')) {
		$args = [];
		if(!Supporters_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Supporters', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Supporters', $recID, calculated_fields()['Supporters']);

	// hook: Supporters_after_insert
	if(function_exists('Supporters_after_insert')) {
		$res = sql("SELECT * FROM `Supporters` WHERE `ID`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!Supporters_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('Supporters', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) Supporters_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function Supporters_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// copy Donations
	$res = sql("SELECT * FROM `Donations` WHERE `SupporterID`='{$safe_sid}'", $eo);
	while($row = db_fetch_assoc($res)) {
		$data = [
			'SelectedID' => $row['ID'],
			'DonationDateDay' => ($row['DonationDate'] ? substr($row['DonationDate'], 8, 2) : ''),
			'DonationDateMonth' => ($row['DonationDate'] ? substr($row['DonationDate'], 5, 2) : ''),
			'DonationDateYear' => ($row['DonationDate'] ? substr($row['DonationDate'], 0, 4) : ''),
			'Amount' => $row['Amount'],
			'Description' => $row['Description'],
			'SupporterID' => $destination_id,
			'CampaignID' => $row['CampaignID'],
			'Paytype' => $row['Paytype'],
			'Number' => $row['Number'],
			'TransNo' => $row['TransNo'],
			'Matching' => $row['Matching'],
			'Anonymous' => $row['Anonymous'],
			'Acknowledged' => $row['Acknowledged'],
			'Notes' => $row['Notes'],
			'MemoryOf' => $row['MemoryOf'],
			'HonorOf' => $row['HonorOf'],
		];

		$ch = curl_insert_handler('Donations', $data);
		if($ch !== false) $requests[] = $ch;
	}

	// copy Notes
	$res = sql("SELECT * FROM `Notes` WHERE `SupporterID`='{$safe_sid}'", $eo);
	while($row = db_fetch_assoc($res)) {
		$data = [
			'SelectedID' => $row['ID'],
			'SupporterID' => $destination_id,
			'Date' => $row['Date'],
			'Note' => $row['Note'],
		];

		$ch = curl_insert_handler('Notes', $data);
		if($ch !== false) $requests[] = $ch;
	}

	// copy MatchingFunds
	$res = sql("SELECT * FROM `MatchingFunds` WHERE `SupporterID`='{$safe_sid}'", $eo);
	while($row = db_fetch_assoc($res)) {
		$data = [
			'SelectedID' => $row['ID'],
			'OrganizationName' => $row['OrganizationName'],
			'Amount' => $row['Amount'],
			'SupporterID' => $destination_id,
			'DateSubmitted' => $row['DateSubmitted'],
			'DateReceived' => $row['DateReceived'],
			'Notes' => $row['Notes'],
			'DonationID' => $row['DonationID'],
			'Attachment' => '',
		];

		$ch = curl_insert_handler('MatchingFunds', $data);
		if($ch !== false) $requests[] = $ch;
	}

	// launch requests, asynchronously
	curl_batch($requests);
}

