function Supporters_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('Supporters', $selected_id, 'edit')) return false;

	$data = [
		'FirstName' => Request::val('FirstName', ''),
		'LastName' => Request::val('LastName', ''),
		'SpouseName' => Request::val('SpouseName', ''),
		'Business' => Request::val('Business', ''),
		'Address1' => Request::val('Address1', ''),
		'Address2' => Request::val('Address2', ''),
		'City' => Request::val('City', ''),
		'State' => Request::val('State', ''),
		'Zip' => Request::val('Zip', ''),
		'Country' => Request::val('Country', ''),
		'Phone' => Request::val('Phone', ''),
		'Cell' => Request::val('Cell', ''),
		'Email' => Request::val('Email', ''),
		'Status' => Request::val('Status', ''),
		'ContactMethod' => Request::multipleChoice('ContactMethod', ''),
		'Grouping' => Request::val('Grouping', ''),
	];

	// get existing values
	$old_data = getRecord('Supporters', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: Supporters_before_update
	if(function_exists('Supporters_before_update')) {
		$args = ['old_data' => $old_data];
		if(!Supporters_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'Supporters', 
		backtick_keys_once($set), 
		['`ID`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="Supporters_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('Supporters', $data['selectedID'], calculated_fields()['Supporters']);

	// hook: Supporters_after_update
	if(function_exists('Supporters_after_update')) {
		$res = sql("SELECT * FROM `Supporters` WHERE `ID`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['ID'];
		$args = ['old_data' => $old_data];
		if(!Supporters_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='Supporters' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

